package main

import (
	"fmt"
	"reflect"
)

func main() {
	fmt.Println(checkInclusion("ab", "eidbaooo"))
	fmt.Println(checkInclusion("ab", "eiboaoo"))
	fmt.Println(checkInclusion("adc", "dcda"))
	fmt.Println(checkInclusion("hello", "ooolleoooleh"))
	fmt.Println(checkInclusion("",
		""))
}

func checkInclusion(s1 string, s2 string) bool {

	char_counts := map[byte]int{}
	for _, ch := range s1 {
		char_counts[byte(ch)]++
	}

	window_counts := map[byte]int{}

	// window_counts should be equal to char_counts
	for i, j := 0, 0; ; {
		if reflect.DeepEqual(char_counts, window_counts) {
			return true
		}
		if j == len(s2) {
			break
		}
		if _, ok := char_counts[s2[j]]; !ok {
			// reset window counts
			window_counts = map[byte]int{}
			j++
			i = j
		} else {
			// check counts equality
			if char_counts[s2[j]] == window_counts[s2[j]] {
				// shrink window
				for s2[i] != s2[j] {
					window_counts[s2[i]]--
					i++
				}
				i++
			} else {
				window_counts[s2[j]]++
			}
			j++
		}
	}

	return false
}
